package com.ishop.mapper;import com.ishop.pojo.TcGoods;import org.apache.ibatis.annotations.Param;import tk.mybatis.mapper.common.Mapper;import java.util.List;import java.util.Map;public interface TcGoodsMapper extends Mapper<TcGoods> {    /**     * 获取所有未被删除的商品列表     * @return 返回获取的商品列表     */    List<TcGoods> selectList(@Param("goodsName") String goodsName);    /**     * 根据一级分类id模糊查询热销商品     * @param oneCatId 一级分类id     * @return 返回获取的热销商品集合     */    List<TcGoods> getRecomGoodsLikeOneCatId(@Param(value="oneCatId") int oneCatId);    /**     * 根据商品id删除商品信息     * @param goodsId     * @return     */    boolean deleteByGoodsId(@Param(value="goodsId") String goodsId);    /**     * 根据商品id来修改商品上下架状态     * @Param goodsId 商品id     *         status  待修改的状态     * @return 返回修改的结果     */    boolean editGoodsSale(@Param(value="goodsId") String goodsId,@Param(value="status") String status );    /**     * 根据商品id来修改商品推荐状态     * @Param goodsId 商品id     *         status  待修改的状态     * @return 返回修改的结果     */    boolean editGoodsRecom(@Param(value = "goodsId") String goodsId,@Param(value = "status") String status );}